import React, { useState } from "react";
import { StyleSheet, Text, View, ScrollView } from "react-native";
import { Input, Card, Button } from "react-native-elements";
import DatePicker from 'react-native-datepicker';

export default function NewPatient() {

  const [room, setRoom] = useState();
  const [department, setDepartment] = useState();
  const [name, setName] = useState();
  const [birthdate, setBirthdate] = useState(new Date());

  return (
    <View style={styles.container}>
      <ScrollView>
        <Text style={styles.title}>New Patient</Text>
        <Card>
          <Input label="Room" />
          <Input label="Department" />
          <Input label="Name" />
          <Input label="ID" />
          <Text style={{fontSize: 16, marginBottom: 10, fontWeight: 'bold', color: '#7b8797'}}>Birth Date</Text>
          <DatePicker
          style={{width: '100%', marginBottom: 15}}
          date={birthdate}
          showIcon={false}
          mode="date"
          placeholder="Birth Date"
          format="YYYY-MM-DD"
          confirmBtnText="Confirm"
          cancelBtnText="Cancel"
          onDateChange={(date) => {setBirthdate(date)}}
          />
          <Input label="Contact name" />
          <Input label="Contact phone" />
          <Input
            label="Arrival date"
            value={new Date().toLocaleDateString()}
            disabled
          />
          <Button title="Create" />
        </Card>
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#ffffff",
    justifyContent: "center",
    height: "90%"
  },
  title: {
    color: "#000000",
    fontSize: 25,
    paddingHorizontal: 10,
    textAlign: "center"
  }
});

const claculateAge = birthdate => {
  var birthDate = new Date(birthdate);
  var difference = Date.now() - birthDate.getTime();
  var ageDate = new Date(difference);
  return Math.abs(ageDate.getUTCFullYear() - 1970);
};
