import React, { useState } from 'react';
import { StyleSheet, Text, View, Picker, ScrollView, Button, TouchableOpacity } from 'react-native';
/* import { Picker } from '@react-native-picker/picker'; */
import departmentsJson from "../data/departments.json";
import { NativeRouter, Route, Link } from "react-router-native";


// const getDepartments = () => {

// }

const Departments = (setStage) => {
    return (
        <View style={styles.departments}>
            <Text style={styles.mainTitle}>Departments</Text>
            <ScrollView style={styles.scroll}>
                {departmentsJson.data.map((dep) => {
                    return (
                        <TouchableOpacity key={dep.id} style={styles.depButton}  onPress={setStage}>
                            <Text style={styles.text}>{dep.name}</Text>
                        </TouchableOpacity>
                    )
                })
                }

            </ScrollView>
        </View>
    );
}

const styles = StyleSheet.create({
    departments: {
        backgroundColor: '#F0F0F0',
        width: '100%',
        height: '90%',
        justifyContent: "center",
        alignItems: "center",
    },
    mainTitle: {
        fontSize: 50,
        marginBottom: 50,
        marginTop: 20,
        fontWeight: "bold",
    },

    text: {
        color: "#000000",
        fontSize: 30
    },

    depButton: {
        height: 50,
        backgroundColor: "lightblue",
        marginBottom: 40,
        width: 300,
        alignItems: "center",
        borderRadius: 22,


    },
});

export default Departments
